#region Helper Functions function Log-Params { param($Invocation) $line='' foreach($key in $Invocation.BoundParameters.Keys) {$line += "$key=$($Invocation.BoundParameters[$key]) "} Log-Event $START_EVENT_ID $EVENT_TYPE_INFORMATION ("Starting script. Invocation Name:$($Invocation.InvocationName)`n Parameters`n $line") $TRACE_INFO }
function Log-Event { param($eventID, $eventType, $msg, $level)
Write-Verbose ("Logging event. " + $SCRIPT_NAME + " EventID: " + $eventID + " eventType: " + $eventType + " Version:" + $SCRIPT_VERSION + " --> " + $msg) if($level -le $P_TraceLevel) { Write-Host ("Logging event. " + $SCRIPT_NAME + " EventID: " + $eventID + " eventType: " + $eventType + " Version:" + $SCRIPT_VERSION + " --> " + $msg) $g_API.LogScriptEvent($SCRIPT_NAME,$eventID,$eventType, ($msg + "`n" + "Version :" + $SCRIPT_VERSION)) } }
Function Throw-EmptyDiscovery { param($SourceId, $ManagedEntityId)
$oDiscoveryData = $g_API.CreateDiscoveryData(0, $SourceId, $ManagedEntityId) Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING "Exiting with empty discovery data" $TRACE_INFO $oDiscoveryData If($traceLevel -eq $TRACE_DEBUG) { #just for debug proposes when launched from command line does nothing when run inside OpsMgr Agent $g_API.Return($oDiscoveryData) } }
Function Throw-KeepDiscoveryInfo { param($SourceId, $ManagedEntityId) $oDiscoveryData = $g_API.CreateDiscoveryData(0,$SourceId,$ManagedEntityId) #Instead of Snapshot discovery, submit Incremental discovery data $oDiscoveryData.IsSnapshot = $false Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING "Exiting with null non snapshot discovery data" $TRACE_INFO $oDiscoveryData If($traceLevel -eq $TRACE_DEBUG) { #just for debug proposes when launched from command line does nothing when run inside OpsMgr Agent $g_API.Return($oDiscoveryData) } } #endregion
#Start by setting up API object. $P_TraceLevel = $TRACE_VERBOSE $g_Api = New-Object -comObject 'MOM.ScriptAPI' #$g_RegistryStatePath = "HKLM\" + $g_API.GetScriptStateKeyPath($SCRIPT_NAME)
$dtStart = Get-Date $P_TraceLevel = $traceLevel Log-Params $MyInvocation
try { if (!(get-Module -Name Hyper-v)) {Import-Module Hyper-v}
if (!(get-command -Module Hyper-V -Name Get-VM -ErrorAction SilentlyContinue)) { Log-Event $START_EVENT_ID $EVENT_TYPE_WARNING ("Get-VM Commandlet doesn't exist.") $TRACE_WARNING Throw-EmptyDiscovery }
$vms = Get-VM | where {$_.ReplicationMode.Value__ -ne 0} #use the enum codes instead of labels 0 = 'None' $discoveryData = $g_api.CreateDiscoveryData(0, $sourceId, $managedEntityId) foreach($vm in $vms) { $replica = Get-VMReplication -VM $vm #I use labels and codes for discovery so they're UI friendly, if in the future I need to group objects I will add a code property. Labels are locale dependent $oInstance = $discoveryData.CreateClassInstance("$MPElement[Name='QND.Hyperv.2012R2.ReplicaVM']$") $oInstance.AddProperty("$MPElement[Name='QND.Hyperv.2012R2.VM']/VirtualMachineId$", $vm.VMId.ToString()) $oInstance.AddProperty("$MPElement[Name='QND.Hyperv.2012R2.ReplicaVM']/PrimaryServerName$", $replica.PrimaryServerName) $oInstance.AddProperty("$MPElement[Name='QND.Hyperv.2012R2.ReplicaVM']/ReplicaServerName$", $replica.ReplicaServerName) $oInstance.AddProperty("$MPElement[Name='QND.Hyperv.2012R2.ReplicaVM']/ReplicationMode$", $vm.ReplicationMode.ToString()) $oInstance.AddProperty("$MPElement[Name='QND.Hyperv.2012R2.ReplicaVM']/RelationshipType$", $replica.RelationshipType.ToString()) $oInstance.AddProperty("$MPElement[Name='QND.Hyperv.2012R2.ReplicaVM']/FrequencySec$", $replica.FrequencySec) $discoveryData.AddInstance($oInstance) } $discoveryData
Log-Event $STOP_EVENT_ID $EVENT_TYPE_SUCCESS ("has completed successfully in " + ((Get-Date)- ($dtstart)).TotalSeconds + " seconds.") $TRACE_INFO } Catch [Exception] { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING ("Main " + $Error) $TRACE_WARNING write-Verbose $("TRAPPED: " + $_.Exception.GetType().FullName); Write-Verbose $("TRAPPED: " + $_.Exception.Message); }

ProbeAction.ScriptBody:
                  #TO SHOW VERBOSE MESSAGES SET $VerbosePreference="continue"
#SET ErrorLevel to 5 so show discovery info

#*************************************************************************
