Function Get-SnapshotTree() { param($vm,[hashtable] $tree=@{}) if ($vm.ParentSnapshotId -ne $null) { #$tree += $vm.ParentSnapShotId $parent = Get-VMSnapshot -Id $vm.ParentSnapShotId $tree.Add($vm.ParentSnapshotId, @($parent.CreationTime,$parent.Name)) Get-SnapshotTree -vm $parent -tree $tree | Out-Null #$parentTree = Get-SnapshotTree -vm $parent -tree @{} #$tree += $parentTree # or # Get-SnapshotTree -vm $parent -tree $tree # # } return $tree }
#Start by setting up API object. $P_TraceLevel = $TRACE_VERBOSE $g_Api = New-Object -comObject 'MOM.ScriptAPI' #$g_RegistryStatePath = "HKLM\" + $g_API.GetScriptStateKeyPath($SCRIPT_NAME)
$dtStart = Get-Date $P_TraceLevel = $traceLevel Log-Params $MyInvocation
try { if (!(get-Module -Name Hyper-v)) {Import-Module Hyper-v}
if (!(get-command -Module Hyper-V -Name Get-VM -ErrorAction SilentlyContinue)) { Log-Event $START_EVENT_ID $EVENT_TYPE_WARNING ("Get-VM Commandlet doesn't exist.") $TRACE_WARNING Exit 1; }
$vms = get-vm # | where {$_.ParentSnapshotId -ne $null}
foreach($vm in $vms) { $snapshotAgeHours = 0 $message='' if ($vm.ParentSnapshotID -ne $null) { $snapshots = Get-SnapshotTree -VM $vm foreach($snapKey in $snapshots.Keys) { if ($snapshots[$snapKey][0] -le [DateTime]'1900-01-01') { $snapConfig = Get-Item -Path "$($vm.ConfigurationLocation)\Snapshots\$($snapKey).xml" $snapshots[$snapKey][0] = $snapCOnfig.CreationTime } $message+="$($snapshots[$snapKey][1]) - $($snapshots[$snapKey][0]) `n" if ( $snapshotAgeHours -lt ([DateTime]::Now - $snapshots[$snapKey][0]).TotalHours) {$snapshotAgeHours=([DateTime]::Now - $snapshots[$snapKey][0]).TotalHours } } } $bag = $g_api.CreatePropertyBag() $bag.AddValue('VMId',$vm.VMId.ToString()) $bag.AddValue('OldestSnapshotAgeHours',$snapshotAgeHours ) $bag.AddValue('SnapshotHistory',$message) $bag Log-Event $START_EVENT_ID $EVENT_TYPE_INFO ("$($vm.VMName) has been processed `n $message") $TRACE_DEBUG } Log-Event $STOP_EVENT_ID $EVENT_TYPE_SUCCESS ("has completed successfully in " + ((Get-Date)- ($dtstart)).TotalSeconds + " seconds.") $TRACE_INFO } Catch [Exception] { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING ("Main " + $Error) $TRACE_WARNING write-Verbose $("TRAPPED: " + $_.Exception.GetType().FullName); Write-Verbose $("TRAPPED: " + $_.Exception.Message); }
#$snapshotFile=$vm.SnapshotFileLocation #$configFileLocartion="$($vm.ConfigurationsFileLocation)\Snapshots"
foreach($v in $vms) { $snapshots = Get-SnapshotTree -VM $v $snapConfig = Get-Item -Path "$($vm.ConfigurationLocation)\Snapshots\$($snap.Id).xml" $snapCOnfig.CreationTime }

