Function NullIsZero { param($value) if(! $value) {return 0} return $value } #Start by setting up API object. $P_TraceLevel = $TRACE_VERBOSE $g_Api = New-Object -comObject 'MOM.ScriptAPI' #$g_RegistryStatePath = "HKLM\" + $g_API.GetScriptStateKeyPath($SCRIPT_NAME)
$dtStart = Get-Date $P_TraceLevel = $traceLevel Log-Params $MyInvocation
try { if (!(get-Module -Name Hyper-v)) {Import-Module Hyper-v}
