#$vms = @(gwmi Msvm_ComputerSystem -namespace "root\virtualization\v2" | where {$_.ReplicationMode -ne 0 -and $_.ReplicationMode -ne $null}) $vms=Get-VM foreach($vm in $vms) { try { $HardDrives = $vm.HardDrives $VHDs = Get-VHD -VMId $vm.VMId -ErrorAction SilentlyContinue $TotalCurrentSize = 0 $TotalMaxSize = 0 $TotalMinSize = 0 foreach($hd in $HardDrives) { $vhd = $VHDs | where {$_.Path -ieq $hd.Path}
if($vhd) { $TotalCurrentSize = $TotalCurrentSize + $vhd.FileSize $TotalMaxSize = $TotalMaxSize + $vhd.Size $TotalMinSize = $TotalMinSize + $vhd.MinimumSize $minSizeGB = [math]::Round((NullIsZero ($vhd.MinimumSize/1GB)),2) } } $TotalCurrentSizeGB = [math]::Round((NullIsZero ($TotalCurrentSize/1GB)),2) $TotalMaxSizeGB = [math]::Round((NullIsZero ($TotalMaxSize/1GB)),2) $TotalMinSizeGB = [math]::Round((NullIsZero ($TotalMinSize/1GB)),2) $bag = $g_api.CreatePropertyBag() $bag.AddValue('VMId',$VM.VMId.ToString()) $bag.AddValue('VMName',$VM.VMName) $bag.AddValue('CurrentSizeGB', $TotalCurrentSizeGB) $bag.AddValue('MaxSizeGB',$TotalMaxSizeGB) $bag.AddValue('MinSizeGB',$TotalMinSizeGB) $bag Log-Event $START_EVENT_ID $EVENT_TYPE_INFO ("$($vm.Name) has been processed") $TRACE_VERBOSE } Catch [Exception] { Log-Event $START_EVENT_ID $EVENT_TYPE_WARNING ("$($vm.Name) error getting disk info $($Error[0].Exception)") $TRACE_WARNING } }
Log-Event $STOP_EVENT_ID $EVENT_TYPE_SUCCESS ("has completed successfully in " + ((Get-Date)- ($dtstart)).TotalSeconds + " seconds.") $TRACE_INFO } Catch [Exception] { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING ("Main " + $($Error[0].Exception)) $TRACE_WARNING write-Verbose $("TRAPPED: " + $_.Exception.GetType().FullName); Write-Verbose $("TRAPPED: " + $_.Exception.Message); }

