try { if (!(get-Module -Name Hyper-v)) {Import-Module Hyper-v}
if (!(get-command -Module Hyper-V -Name Get-VM -ErrorAction SilentlyContinue)) { Log-Event $START_EVENT_ID $EVENT_TYPE_WARNING ("Get-VM Commandlet doesn't exist.") $TRACE_WARNING Exit 1; }
if ($VMGuid -ine 'ignore') { #here we're in atask targeted at a specific VM $vm = Get-VM | where {$_.VMId -ieq $VMGuid} Write-Host "NYI!" exit; }
#$vms = @(gwmi Msvm_ComputerSystem -namespace "root\virtualization\v2" | where {$_.ReplicationMode -ne 0 -and $_.ReplicationMode -ne $null}) $vms=Get-VM foreach($vm in $vms) { try { $HardDrives = $vm.HardDrives $VHDs = Get-VHD -VMId $vm.VMId -ErrorAction SilentlyContinue foreach($hd in $HardDrives) { $vhd = $VHDs| where {$_.Path -ieq $hd.Path} if($vhd) { $currentSizeGB = [math]::Round((NullIsZero ($vhd.FileSize/1GB)),2) $maxSizeGB = [math]::Round((NullIsZero ($vhd.Size/1GB)),2) $minSizeGB = [math]::Round((NullIsZero ($vhd.MinimumSize/1GB)),2) $fragPerc = NullIsZero ($vhd.FragmentationPercentage) } else { $currentSizeGB = -1 $maxSizeGB = -1 $minSizeGB = -1 $fragPerc = -1 } $bag = $g_api.CreatePropertyBag() $bag.AddValue('VirtualDiskId',$hd.Id) $bag.AddValue('SizeGB', $currentSizeGB) $bag.AddValue('MaxSizeGB',$maxSizeGB) $bag.AddValue('MinSizeGB',$minSizeGB) $bag.AddValue('FragPerc',$fragPerc) $bag Log-Event $START_EVENT_ID $EVENT_TYPE_INFO ("$($vm.Name) - $($hd.Id) frag level $fragPerc") $TRACE_VERBOSE } Log-Event $START_EVENT_ID $EVENT_TYPE_INFO ("$($vm.Name) has been processed") $TRACE_VERBOSE } Catch [Exception] { Log-Event $START_EVENT_ID $EVENT_TYPE_WARNING ("$($vm.Name) error getting disk info $($Error[0].Exception)") $TRACE_WARNING } }
Log-Event $STOP_EVENT_ID $EVENT_TYPE_SUCCESS ("has completed successfully in " + ((Get-Date)- ($dtstart)).TotalSeconds + " seconds.") $TRACE_INFO } Catch [Exception] { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING ("Main " + $($Error[0].Exception)) $TRACE_WARNING write-Verbose $("TRAPPED: " + $_.Exception.GetType().FullName); Write-Verbose $("TRAPPED: " + $_.Exception.Message); }

