Function Process-VM { param($vm) $lastCheck = [xml] (Get-Content -Path "$($vm.Path)\$MGGuid-UpTimeCheck.xml" -ErrorAction SilentlyContinue) $vmUpTime = NullIsZero $vm.Uptime.TotalHours if(! $lastCheck) { $lastCheck = "<LastUptimeCheck><Uptime>$vmUpTime</Uptime><Check>$timeNow</Check></LastUptimeCheck>" Log-Event $START_EVENT_ID $EVENT_TYPE_INFORMATION ("No previous uptime data exists for $($vm.Name)") $TRACE_INFO $lastCheck | Out-File -FilePath "$($vm.Path)\$MGGuid-UpTimeCheck.xml" -Force return; } $lastTotalHours = [double] ($lastCheck.LastUptimeCheck.Uptime) $lastDateCheck = [DateTime] ($lastCheck.LastUptimeCheck.Check)
$uptimeInperiod = $vm.Uptime.TotalHours - $lastTotalHours if($uptimeInPeriod -le 0) {$uptimeInPeriod = $vm.Uptime.TotalHours} $elapsedInPeriod = ($timeNow - $lastDateCheck).TotalHours $percUptime = [math]::Round($uptimeInperiod/$elapsedInPeriod * 100,2) if($percUptime -gt 100){$percUptime=100.00} $lastCheck = "<LastUptimeCheck><Uptime>$vmUpTime</Uptime><Check>$TimeNow</Check></LastUptimeCheck>" $lastCheck | Out-File -FilePath "$($vm.Path)\$MGGuid-UpTimeCheck.xml" -Force
$bag = $g_api.CreatePropertyBag() $bag.AddValue('VMId',$vm.VMId.ToString()) $bag.AddValue('PercUptime', (NullIsZero $percUptime)) $bag.AddValue('TotalUptime',(NullIsZero $vm.Uptime.TotalHours)) $bag.AddValue('UptimeInPeriod',(NullIsZero $uptimeInperiod)) #now return the disk data if we have any $bag Log-Event $START_EVENT_ID $EVENT_TYPE_INFO ("$($vm.Name) has been processed, uptime perc is $percUptime") $TRACE_VERBOSE } #Start by setting up API object. $P_TraceLevel = $TRACE_VERBOSE $g_Api = New-Object -comObject 'MOM.ScriptAPI' $g_RegistryStatePath = "HKLM\" + $g_API.GetScriptStateKeyPath($SCRIPT_NAME) + "\$MGGuid"
$dtStart = Get-Date $P_TraceLevel = $traceLevel Log-Params $MyInvocation
try { if (!(get-Module -Name Hyper-v)) {Import-Module Hyper-v}
if (!(get-command -Module Hyper-V -Name Get-VM -ErrorAction SilentlyContinue)) { Log-Event $START_EVENT_ID $EVENT_TYPE_WARNING ("Get-VM Commandlet doesn't exist.") $TRACE_WARNING Exit 1; }
if ($VMGuid -ine 'ignore') { #here we're in atask targeted at a specific VM $vm = Get-VM | where {$_.VMId -ieq $VMGuid} if($vm) {Process-VM $vm} exit; }
#$vms = @(gwmi Msvm_ComputerSystem -namespace "root\virtualization\v2" | where {$_.ReplicationMode -ne 0 -and $_.ReplicationMode -ne $null}) $timeNow = [DateTime]::Now $VMs=Get-VM foreach($vm in $VMs) { try { Process-VM $vm } Catch [Exception] { Log-Event $START_EVENT_ID $EVENT_TYPE_WARNING ("$($vm.Name) error getting Uptime info $($Error[0].Exception)") $TRACE_WARNING } }
Log-Event $STOP_EVENT_ID $EVENT_TYPE_SUCCESS ("has completed successfully in " + ((Get-Date)- ($dtstart)).TotalSeconds + " seconds.") $TRACE_INFO } Catch [Exception] { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING ("Main " + $($Error[0].Exception)) $TRACE_WARNING write-Verbose $("TRAPPED: " + $_.Exception.GetType().FullName); Write-Verbose $("TRAPPED: " + $_.Exception.Message); }

#=========================================================================================== # AUTHOR: Tao Yang # Script Name: VMPreferredHostWMIProbe.ps1 # DATE: 03/07/2012 # Version: 1.5 # COMMENT: - Script to check if Hyper-V guest VM is located on the preferred host #=========================================================================================== #get host name $HostName = $env:COMPUTERNAME
#This Script $ScriptName = "VMPreferredHostWMIProbe.ps1" #MOM API $oAPI = New-Object -ComObject "MOM.ScriptAPI"
#Debugging Log $OAPI.LogScriptEvent($ScriptName,11000,0,"Hyper-V Cluster VM Preferred Host Probe script is being executed on $HostName`...")
#PS array for virtual machines that are located on the wrong host $arrVMsOnWrongHost = @() $arrVMsOnPreferredHost = @() $arrPreferredHostsNotConfigured = @() #$arrIncorrectPreferredOwner = @()
#Get virtual machines cluster resources that are currently hosted on the local cluster node (this computer) $arrVirtualMachines = Get-WmiObject -Namespace root\MSCluster -Query "Select * from MSCluster_Resource Where Type = 'Virtual Machine'" $bAllVMsOnPreferredHost = $true $bAllVMsPreferredHostConfigured = $true
foreach ($VirtualMachine in $arrVirtualMachines) { $VMResourceGroup = Get-WmiObject -Namespace root\MSCluster -Query "Associators of {MSCluster_Resource.Name='$($VirtualMachine.Name)'} WHERE AssocClass = MSCluster_ResourceGroupToResource" $VirtualMachineName = $VMResourceGroup.Name $CurrentResourceOwner = Get-WmiObject -Namespace root\MSCluster -Query "Associators of {MSCluster_ResourceGroup.Name='$($VMResourceGroup.Name)'} WHERE AssocClass = MSCluster_NodeToActiveGroup" $arrPreferredOwners = Get-WmiObject -Namespace root\MSCluster -Query "Associators of {MSCluster_ResourceGroup.Name='$($VMResourceGroup.Name)'} WHERE AssocClass = MSCluster_ResourceGroupToPreferredNode" #Determine if the current resource owner is one of the preferred owners if ($arrPreferredOwners) { $IsOnPreferredHost = $false Foreach ($PreferredOwner in $arrPreferredOwners) { if ($PreferredOwner.Name -ieq $CurrentResourceOwner.Name) { $IsOnPreferredHost = $true #add the VM to $arrVMsOnPreferredHost $arrVMsOnPreferredHost += $VirtualMachineName $bAllVMsPreferredHostConfigured = $false } } } else { #Preferred host not configured for this VM. consider as is on preferred host $IsOnPreferredHost = $true #Add the VM to $arrPreferredHostsNotConfigured $arrPreferredHostsNotConfigured += $VirtualMachineName }
#If the VM is not on the preferred owner, add to the $arrVMsOnWrongHost array and change $bAllVMsOnPreferredOwner to False If (!$IsOnPreferredHost) { $bAllVMsOnPreferredHost = $false $arrVMsOnWrongHost += $VirtualMachineName } }
#Convert VM On Wrong Host array to string - separated by ";" $strVMsOnWrongHost = [system.String]::Join(";", $arrVMsOnWrongHost)
#Convert VM On Preferred Host array to string - separated by ";" $strVMsOnPreferredHost = [system.String]::Join(";", $arrVMsOnPreferredHost)
#Convert Preferred Host Not Set array to string - separated by ";" $strPreferredHostsNotConfigured = [system.String]::Join(";", $arrPreferredHostsNotConfigured)
#MOM API PropertyBag $oBag = $oAPI.CreatePropertyBag() $oBag.AddValue('VMsOnWrongHost', $strVMsOnWrongHost) $oBag.AddValue('VMsOnPreferredHost', $strVMsOnPreferredHost) $oBag.AddValue('PreferredHostsNotConfigured', $strPreferredHostsNotConfigured) $oBag.AddValue('AllVMsOnPreferredHost', $bAllVMsOnPreferredHost) $oBag.AddValue('AllVMsPreferredHostConfigured', $bAllVMsPreferredHostConfigured)
#Debugging Log if ($bAllVMsOnPreferredHost) { $OAPI.LogScriptEvent($ScriptName,11001,0,"All virtual machines are on preferred hosts: $strVMsOnPreferredHost") } else { $OAPI.LogScriptEvent($ScriptName,11003,2,"At least 1 virtual machine is not on preferred hosts") }
if ($strVMsOnPreferredHost.length -gt 0) { $OAPI.LogScriptEvent($ScriptName,11002,0,"The following virtual machines are on the preferred host: $strVMsOnPreferredHost") }
if ($strVMsOnWrongHost.length -gt 0) { $OAPI.LogScriptEvent($ScriptName,11004,2,"The following virtual machines are NOT on the preferred host: $strVMsOnWrongHost") }
#return property bag $oBag #$oAPI.Return($oBag)

